mapper.update:

UPDATE comtcadministcoderecptnlog
SET OCCRRNC_DE=#{occrrncDe}
	, ADMINIST_ZONE_SE=#{administZoneSe}
	, ADMINIST_ZONE_CODE=#{administZoneCode}
	, OPERT_SN=#{opertSn}
	, CHANGE_SE_CODE=#{changeSeCode}
	, PROCESS_SE=#{processSe}
	, ADMINIST_ZONE_NM=#{administZoneNm}
	, LOWEST_ADMINIST_ZONE_NM=#{lowestAdministZoneNm}
	, CTPRVN_CODE=#{ctprvnCode}
	, SIGNGU_CODE=#{signguCode}
	, EMD_CODE=#{emdCode}
	, LI_CODE=#{liCode}
	, CREAT_DE=#{creatDe}
	, ABL_DE=#{ablDe}
	, ABL_ENNC=#{ablEnnc}
	, FRST_REGISTER_ID=#{frstRegisterId}
	, FRST_REGIST_PNTTM=#{frstRegistPnttm}
	, LAST_UPDUSR_ID=#{lastUpdusrId}
	, LAST_UPDT_PNTTM=#{lastUpdtPnttm}
			WHERE OCCRRNC_DE=#{occrrncDe}
					AND ADMINIST_ZONE_SE=#{administZoneSe}
					AND ADMINIST_ZONE_CODE=#{administZoneCode}
					AND OPERT_SN=#{opertSn}

mapper.delete:

DELETE FROM comtcadministcoderecptnlog 
			WHERE OCCRRNC_DE=#{occrrncDe}
					AND ADMINIST_ZONE_SE=#{administZoneSe}
					AND ADMINIST_ZONE_CODE=#{administZoneCode}
					AND OPERT_SN=#{opertSn}

mapper.select:
SELECT OCCRRNC_DE , ADMINIST_ZONE_SE , ADMINIST_ZONE_CODE , OPERT_SN , CHANGE_SE_CODE , PROCESS_SE , ADMINIST_ZONE_NM , LOWEST_ADMINIST_ZONE_NM , CTPRVN_CODE , SIGNGU_CODE , EMD_CODE , LI_CODE , CREAT_DE , ABL_DE , ABL_ENNC , FRST_REGISTER_ID , FRST_REGIST_PNTTM , LAST_UPDUSR_ID , LAST_UPDT_PNTTM FROM comtcadministcoderecptnlog WHERE 1=1
AND OCCRRNC_DE = #{searchKeyword}
AND ADMINIST_ZONE_SE LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY OCCRRNC_DE DESC , ADMINIST_ZONE_SE DESC , ADMINIST_ZONE_CODE DESC , OPERT_SN DESC limit #{recordCountPerPage} offset #{firstIndex}

SELECT COUNT(*) totcnt FROM comtcadministcoderecptnlog WHERE 1=1
AND OCCRRNC_DE = #{searchKeyword}
AND ADMINIST_ZONE_SE LIKE CONCAT('%',#{searchKeyword},'%')
